package week1.day2;

public class LearnForLoop {
	
	//My task is print number from 1 t0 5

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for (int i=1; i<5;i++) {
	System.out.println(i);
}
 System.out.println("-----------------------------");
//reverse for loop
 // to print the value 5 4 3 2 1 
 for(int i=5; i>=1;i--) {
	 System.out.println(i);
 }
	}

}
